// Access the array of news from the input data
const newsArray = items[0].json.output.news;

// The .map() function loops through each item in the newsArray
return newsArray.map(newsItem => {
  // --- Start of Transformation ---

  // 1. Format the date for the subject line
  const formattedDate = new Date(newsItem.date_published).toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    timeZone: 'UTC' // Use UTC to ensure consistent date parsing
  });

  // 2. Create the new subject line
  const subjectLine = `Today's AI News: ${newsItem.title} | ${formattedDate}`;

  // 3. Build the new JSON object with only the desired fields
  const finalJson = {
    subject_line: subjectLine,
    title: newsItem.title,
    content: newsItem.content,
    source: newsItem.source,
    date_published: newsItem.date_published
  };

  // --- End of Transformation ---

  // Return the newly structured object as a new n8n item
  return {
    json: finalJson
  };
});
