return items.map(item => {
  try {
    const videoLink = item.json['Video Link'];
    let videoId = null;

    if (typeof videoLink === 'string') {
      // Regex to extract the video ID
      const match = videoLink.match(/(?:v=|\/)([0-9A-Za-z_-]{11})/);
      if (match && match[1]) {
        videoId = match[1];
      } else {
        throw new Error('Could not extract video ID from link');
      }
    } else {
      throw new Error('Invalid video link');
    }

    return {
      json: {
        ...item.json,
        videoId,
      }
    };
  } catch (error) {
    return {
      json: {
        ...item.json,
        error: error.message,
      }
    };
  }
});

return items.map(item => {
  const transcriptBlocks = item.json?.data?.transcripts?.hi_auto?.custom || [];

  // Combine all text blocks into one string
  const fullTranscript = transcriptBlocks
    .map(block => block.text)
    .filter(text => typeof text === 'string')
    .join(' '); // or '\n' if you want line breaks

  return {
    json: {
      transcript: fullTranscript
    }
  };
});

