return items.map(item => {
  const rawText = item.json.text || "";

  const flattenedText = rawText
    .replace(/\\n/g, ' ')  // Replace literal \n
    .replace(/\n/g, ' ')   // Replace real newlines
    .replace(/\s+/g, ' ')  // Collapse multiple spaces
    .trim();

  return {
    json: {
      formattedDescription: flattenedText
    }
  };
});

return items.map(item => {
  const raw = item.json.output || "";

  // Step 1: Replace literal \n with a space (to fully eliminate them)
  const noNewlines = raw.replace(/\\n/g, ' ').replace(/\n/g, ' ');

  // Step 2: Split by numbering pattern
  const parts = noNewlines
    .split(/\d+\.\s/)
    .map(p => p.trim())
    .filter(p => p.length > 0);

  // Step 3: Add numbers back cleanly
  const formatted = parts.map((title, i) => `${i + 1}. ${title}`);

  return {
    json: {
      formattedTitles: formatted.join(' ')
    }
  };
});
