const input = items[0].json.output;

// --- Utility functions ---
function extractBetween(text, start, end) {
  const regex = new RegExp(`${start}[\\s\\S]*?(?=${end}|$)`, "i");
  const match = text.match(regex);
  return match ? match[0] : "";
}

function extractField(section, field) {
  const regex = new RegExp(`${field}:\\s*([^\\n<]+)`, "i");
  const match = section.match(regex);
  return match ? match[1].trim() : null;
}

function extractRationale(section, label, nextLabels = []) {
  let pattern;
  if (nextLabels.length > 0) {
    pattern = `<b>${label}:<\/b>[\\s\\S]*?(?=${nextLabels.map(l => `<b>${l}`).join("|")}|$)`;
  } else {
    // take everything until the end of the section
    pattern = `<b>${label}:<\/b>[\\s\\S]*`;
  }
  const regex = new RegExp(pattern, "i");
  const match = section.match(regex);
  if (!match) return null;
  return match[0]
    .replace(new RegExp(`<b>${label}:<\/b>`, "i"), "")
    .replace(/\n+/g, " ")
    .trim();
}

// --- Extract Sections ---
const spotBlock = extractBetween(input, "<b>Spot Recommendations</b>", "<b>Leveraged Recommendations</b>");
const levBlock = extractBetween(input, "<b>Leveraged Recommendations</b>", "$$END$$");

// --- Spot Short-Term ---
const spotShort = extractBetween(spotBlock, "<b>Short-Term</b>", "<b>Long-Term</b>");
const spotShortObj = {
  action: extractField(spotShort, "Action"),
  entryPrice: extractField(spotShort, "Entry Price"),
  stopLoss: extractField(spotShort, "Stop Loss"),
  takeProfit: extractField(spotShort, "Take Profit"),
  rationale: {
    primarySignals: extractRationale(spotShort, "Primary Signals", ["Lagging Indicators", "Sentiment Analysis"]),
    laggingIndicators: extractRationale(spotShort, "Lagging Indicators", ["Sentiment Analysis"]),
    sentiment: extractRationale(spotShort, "Sentiment Analysis") // now until end
  }
};

// --- Spot Long-Term ---
const spotLong = extractBetween(spotBlock, "<b>Long-Term</b>", "$$END$$");
const spotLongObj = {
  action: extractField(spotLong, "Action"),
  entryPrice: extractField(spotLong, "Entry Price"),
  stopLoss: extractField(spotLong, "Stop Loss"),
  takeProfit: extractField(spotLong, "Take Profit"),
  rationale: {
    primarySignals: extractRationale(spotLong, "Primary Signals", ["Lagging Indicators", "Sentiment Analysis"]),
    laggingIndicators: extractRationale(spotLong, "Lagging Indicators", ["Sentiment Analysis"]),
    sentiment: extractRationale(spotLong, "Sentiment Analysis") // fixed
  }
};

// --- Leveraged Short-Term ---
const levShort = extractBetween(levBlock, "<b>Short-Term</b>", "<b>Long-Term</b>");
const levShortObj = {
  position: extractField(levShort, "Position"),
  leverage: extractField(levShort, "Leverage"),
  entryPrice: extractField(levShort, "Entry Price"),
  stopLoss: extractField(levShort, "Stop Loss"),
  takeProfit: extractField(levShort, "Take Profit"),
  rationale: {
    primarySignals: extractRationale(levShort, "Primary Signals", ["Lagging Indicators", "Sentiment Analysis"]),
    laggingIndicators: extractRationale(levShort, "Lagging Indicators", ["Sentiment Analysis"]),
    sentiment: extractRationale(levShort, "Sentiment Analysis") // fixed
  }
};

// --- Leveraged Long-Term ---
const levLong = extractBetween(levBlock, "<b>Long-Term</b>", "$$END$$");
const levLongObj = {
  position: extractField(levLong, "Position"),
  leverage: extractField(levLong, "Leverage"),
  entryPrice: extractField(levLong, "Entry Price"),
  stopLoss: extractField(levLong, "Stop Loss"),
  takeProfit: extractField(levLong, "Take Profit"),
  rationale: {
    primarySignals: extractRationale(levLong, "Primary Signals", ["Lagging Indicators", "Sentiment Analysis"]),
    laggingIndicators: extractRationale(levLong, "Lagging Indicators", ["Sentiment Analysis"]),
    sentiment: extractRationale(levLong, "Sentiment Analysis") // fixed
  }
};

// --- Final JSON ---
return [
  {
    json: {
      spot: {
        shortTerm: spotShortObj,
        longTerm: spotLongObj,
      },
      leveraged: {
        shortTerm: levShortObj,
        longTerm: levLongObj,
      }
    }
  }
];

